mapper.select:

	SELECT *
	FROM
	(
			SELECT rownum rn, a.*
			FROM
						(select * from emp order by empno) a
	)
	WHERE	rn >= #{start} 
	and 	rn <= #{end}

mapper.update:
Update emp
SET ename = #{ename}, job = #{job}, mgr = #{mgr}, sal = #{sal},  hiredate = #{hiredate} , 
 comm = #{comm}, 
	deptno = #{deptno}
WHERE empno = #{empno}
